mapper.select:
        SELECT DISTINCT sys_menu.*
        FROM sys_menu
        LEFT JOIN sys_role_menu ON sys_role_menu.menu_id = sys_menu.id
        LEFT JOIN sys_role ON sys_role_menu.role_id = sys_role.id
        WHERE sys_role.id IN
        
            #{id}
        
        AND sys_role.deleted = 0 AND sys_menu.deleted = 0

        
            AND sys_menu.name like concat('%', #{param.name}, '%')
        

        
            AND sys_menu.status = #{param.status}
        
        ORDER BY sys_menu.id;
